// 	    	fetch(url, {
// 			  "method": "GET",
// 			})
// 			.then(response => response.json())
// 			.then(response => {
// 				this.setState({ details: response.ticket });
// 				this.setState({ loading: false });


// 			})
// 			.catch(err => { console.log(err); 
// 			})
// 	};
// }


function TicketList() {
	const [loading, setLoading] = React.useState(true);
	const [show, setShow] = React.useState(false);
	const [data, setData] = React.useState([]);
	const [ticketid, setId] = React.useState();

	function TagFormatter(cell, row) {

		var tag = []
		for (var i = 0; i < cell.length; i++) {
		    tag.push(<Badge pill bg="secondary" text="light">{ cell[i] } </Badge> );
		}
		return (
			tag
		);
	}

	function DateFormatter(cell, row) {

		return	(
			moment(cell).format("MMM Do YYYY, h:mm:ss a")
		)
	}

	const columns = [
		{
			dataField: "id",
			text:"ID",
			sort: true	
		},
		{
			dataField: "subject",
			text:"Subject",
			sort: true
		},
		{
			dataField: "status",
			text:"Status",
			sort: true
		},
		{
			dataField: "tags",
			text:"Tags",
			sort: true,
			formatter: TagFormatter,
		},
		{
			dataField: "created_at",
			text:"Created",
			sort: true,
			formatter: DateFormatter,
		},
		{
			dataField: "updated_at",
			text:"Updated",
			sort: true,
			formatter: DateFormatter,
		},

	]

	const sizePerPageList = [
		{
		  text: '5', value: 5
		}, {
		  text: '15', value: 15
		}, {
		  text: '25', value: 25
		},
		{
		  text: 'All', value: data.length
		}  
	]

	const ticketEvent = {

		onClick: (e, row, rowIndex) => {
		     // console.log(`clicked on row with index: ${row.id}`);
		     setId(row.id);
		     setShow(true);

		},
	}

	const fromTicketDetail = (childdata) => {
	    setShow(childdata);
	 }

	useEffect(()=>{

		const url = "http://localhost:3000/api/v1/base";
	    	fetch(url, {
			  "method": "GET",
			})
			.then(response => response.json())
			.then(response => {
			  setData(response.tickets); 
			  setLoading(false);
			})
			.catch(err => { console.log(err); 
			})

	}, []);

	return	!loading ? 
			<>

				<Container>	
				 <BootstrapTable hover
			        bootstrap4
			        keyField="id"
			        data={data}
			        columns={columns}
			        rowEvents={ ticketEvent }
			        pagination={paginationFactory({ sizePerPage: 25 , sizePerPageList: sizePerPageList})}
			      />

				</Container>

				{show && <TicketDetails id={ticketid} show={show} onHide={() => setShow(false)}/>}	
			</>
			:

			<Container className="header">
				<Button variant="primary" disabled>
			    <Spinner
			      as="span"
			      animation="grow"
			      size="sm"
			      role="status"
			      aria-hidden="true"
			    />
			    {" "}
			    Loading...
			  </Button>

			</Container>

}


export default TicketList
