import React,{useEffect} from 'react'
import Home from './Home'
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import TicketDetails from './TicketDetails'

class TicketList extends React.Component{
	constructor(props) {
    	super(props);
    	this.state = {
    		show: false,
    		loading: false,
    		data: [],
    	};

    };

    TicketList(){
    		this.setState({ loading: true});

	    	const url = "http://localhost:3000/api/v1/base";
	    	fetch(url, {
			  "method": "GET",
			})
			.then(response => response.json())
			.then(response => {
			  this.setState({ data: response }, () => {
			  console.log(this.state);
			  }); 
			})
			.catch(err => { console.log(err); 
			})
			.finally(() => {
        		this.setState({ loading: false});
        	}); 	


		if (loading) {
		    return <p>Data is loading...</p>;
		}
    }

    componentDidMount() {
      this.TicketList();
  	};

	render () {
		return	(
			<>
				<Container>

				<Table bordered hover>
				  <thead>
				    <tr>
				      <th>ID</th>
				      <th>Subject</th>
				      <th>Status</th>
				      <th>Priority</th>
				      <th>Tags</th>
				      <th>Created</th>
				      <th>Updated</th>
				    </tr>
				  </thead>
				  <tbody>
				  {this.state.data?.tickets?.map(( listValue, index ) => {
		            <tr key={index}>
		              <td>{listValue.id}</td>
		              <td>{listValue.subject}</td>
		              <td>{listValue.status}</td>
		              <td>{listValue.priority}</td>
		              <td>{listValue.priority}</td>
		              <td>{listValue.created_at}</td>
		              <td>{listValue.updated_at}</td>

		            </tr>
		          })}

				  </tbody>
				</Table>
				</Container>

				{this.state.show && <TicketDetails>{this.state.show} </TicketDetails>}

			</>
		)

	}
}

export default TicketList
