import Home from './Home'
import React,{useEffect, useState} from 'react'
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Container from 'react-bootstrap/Container';
import Badge from 'react-bootstrap/Badge';
import Popover from 'react-bootstrap/Popover';
import PopoverContent from 'react-bootstrap/PopoverContent';
import PopoverTitle from 'react-bootstrap/PopoverTitle';
import OverlayTrigger from 'react-bootstrap/OverlayTrigger';

import moment from 'moment';

import './App.css'

function TicketDetails(props){
	const [details,setDetails]=useState([]);
	const [loading, setLoading] = useState(true);

	useEffect(()=>{

		const url = "http://localhost:3000/api/v1/ticket/"+props.id;
	    	fetch(url, {
			  "method": "GET",
			})
			.then(response => response.json())
			.then(response => {
			  setDetails(response.ticket); 
			  setLoading(false);
			})
			.catch(err => { console.log(err); 
			})

	}, []);

	const popover = (
	  <Popover id="popover-basic">
	    <Popover.Title as="h3">Popover right</Popover.Title>
	    <Popover.Content>
	      And here's some <strong>amazing</strong> content. 
	    </Popover.Content>
	  </Popover>
	);


	return (
		!loading ? 
		<>
			<Modal
		      {...props}
		      size="lg"
		      aria-labelledby="contained-modal-title-vcenter"
		      centered
		    >

		      <Modal.Header>
		        <Modal.Title id="contained-modal-title-vcenter">
		          {details.id}.  {details.subject}
		          {" "}{details.status == "open" && <Badge bg="danger" text="light">{details.status}</Badge>}

		        </Modal.Title>

		      </Modal.Header>
		      <Modal.Body>
		        <h5>Description</h5>
		        <p>
		          {details.description}
		        </p>
		        <Badge pill bg="secondary" text="light">{details.tags} </Badge>

		        <OverlayTrigger trigger="click" placement="right" overlay={popover}>
		        	<div>
				    <Button variant="success">Click me to see</Button>
				    </div>
				 </OverlayTrigger>

		      </Modal.Body>
		      <Modal.Footer>
		      	<p className="mr-auto">Created On: {moment(details.created_at).format("LLL")}</p>
		        <Button onClick={props.onHide}>Close</Button>
		      </Modal.Footer>

		    </Modal>
		</>
		:
		<>
		</>
	)
}

export default TicketDetails
