import React,{useEffect, useState} from 'react'
import Home from './Home'
import Table from 'react-bootstrap/Table';
import Container from 'react-bootstrap/Container';
import ProgressBar from 'react-bootstrap/ProgressBar';
import TicketDetails from './TicketDetails'
import './App.css'


import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";


function TicketList() {
	const [show, setShow] = useState(false);
	const [loading, setLoading] = useState(true);
	const [data, setData] = useState([]);
	const [ticketid, setId] = useState();

	function TagFormatter(cell, row) {

		// console.log(cell);
		// console.log(cell[0]);
		for (var tag in tags){
			<Badge bg="danger">{tag}</Badge>
		}
	}


	const columns = [
		{
			dataField: "id",
			text:"ID",
			sort: true	
		},
		{
			dataField: "subject",
			text:"Subject",
			sort: true
		},
		{
			dataField: "status",
			text:"Status",
			sort: true
		},
		{
			dataField: "tags",
			text:"Tags",
			sort: true,
			formatter: TagFormatter,
		},
		{
			dataField: "created_at",
			text:"Created",
			sort: true
		},
		{
			dataField: "updated_at",
			text:"Updated",
			sort: true
		},

	]

	const sizePerPageList = [
		{
		  text: '5', value: 5
		}, {
		  text: '15', value: 15
		}, {
		  text: '25', value: 25
		},
		{
		  text: 'All', value: data.length
		}  
	]

	const ticketEvent = {

		onClick: (e, row, rowIndex) => {
		     // console.log(`clicked on row with index: ${row.id}`);
		     setId(row.id);
		     setShow(true);

		},
	}

	const fromTicketDetail = (childdata) => {
	    setShow(childdata);
	 }

	useEffect(()=>{

		const url = "http://localhost:3000/api/v1/base";
	    	fetch(url, {
			  "method": "GET",
			})
			.then(response => response.json())
			.then(response => {
			  setData(response.tickets); 
			  setLoading(false);
			})
			.catch(err => { console.log(err); 
			})

	}, []);

	return	!loading ? 
			<>

				<Container>	
				 <BootstrapTable hover
			        bootstrap4
			        keyField="id"
			        data={data}
			        columns={columns}
			        rowEvents={ ticketEvent }
			        pagination={paginationFactory({ sizePerPage: 25 , sizePerPageList: sizePerPageList})}
			      />

				</Container>

				{show && <TicketDetails id={ticketid} show={show} onHide={() => setShow(false)}/>}	
			</>
			:
			<Container className="header">
				<h4>Loading Data</h4>
				<ProgressBar animated now={45} />
			</Container>

}


export default TicketList
